import "../Stylesheet/styles.css";
const decimaltopercent=(decimal)=>{
    return (decimal.toFixed(2)+'%')
}

const calculatescore=(scored,total_score)=>{
    return decimaltopercent(scored/total_score)
}

export const Scorecalculator =({name,school,marks,goal})=>(
    <div style={{marginLeft:"auto",marginRight:"auto",width:"fit-content"   }}>
        <h1 style={{color:"maroon"}}>Student Details</h1>
        <div className="name">
           <span>Name:{name}</span> 
        </div>
    
        <div className="school">
           <span>School:{school}</span> 
        </div>
    
        <div className="marks">
           <span>Marks:{marks}</span> 
        </div>
    
        <div className="percentage">
           <span>Percentage:</span> 
           <span>{calculatescore(marks,goal)}</span>
        </div>
    </div>
)
