import React,{Component} from "react";
import Post1 from "./Posts";


class Posts extends Component{
    constructor(props){
        super(props);
        this.state={
            posts:[],
        };
    }


loadposts=()=>{
    fetch("https://jsonplaceholder.typicode.com/posts")
    .then((res)=>res.json())
    .then((data)=>{
       const postlist= data.map((p)=>new Posts(p.userId,p.id,p.title,p.body)
    );

    this.setState({posts:postlist});
})

.catch((err)=>{
    alert("Error fetching posts:"+err.message);
});
};


componentDidMount(){
    this.loadposts();
}

componentDidCatch(error,info){
    alert("An error occured"+error.message);
}

render(){
    return(
        <div>
            <h1>Blog Posts</h1>
            {this.state.posts.map((post)=>(
                <div key={post.id}>
            <h3>{post.title}</h3>
            <p>{post.body}</p>
          </div>
            ))}
        </div>
    );
}
}

export default Posts;
