import React from "react";

const cohorts=(cohort)=>{
    return(
        <div>
            <h1>Cohort Details</h1>
            {cohorts.map((cohort,index)=>(
                <div key='index' className="box">
                    <h3 style={{color:cohort.status==="Ongoing"? "green":blue}}>{cohort.name}</h3>
                    <dl>
                        <dt>
                            Started On
                        </dt>
                        <dd>
                            {cohort.startDate}
                        </dd>
                        <dt>
                            Current Status
                        </dt>
                        <dd>
                            {cohort.status}
                        </dd>
                        <dt>
                            Coach
                        </dt>
                        <dd>
                            {cohort.trainer}
                        </dd>
                    </dl>
                </div>
            ))
            }
        </div>
    )
}

export default cohorts;
